let successRate = toscalar(
    customEvents
    | where timestamp >= ago({TimeRange:duration})
    | where name == "ResumeProcessed"
    | summarize SuccessRate = 100.0 * countif(customDimensions.success == "true") / count()
);
let avgATSScore = toscalar(
    customMetrics
    | where timestamp >= ago({TimeRange:duration})
    | where name == "ATSScore"
    | summarize AvgScore = avg(value)
);
let p95ProcessingTime = toscalar(
    customMetrics
    | where timestamp >= ago({TimeRange:duration})
    | where name == "ResumeProcessingTime"
    | summarize P95Time = percentile(value, 95)
);
let errorRate = toscalar(
    customMetrics
    | where timestamp >= ago({TimeRange:duration})
    | where name == "ResumeProcessingErrors"
    | summarize ErrorRate = sum(value) * 100.0 / 
        toscalar(
            customEvents
            | where timestamp >= ago({TimeRange:duration})
            | where name == "ResumeProcessed"
            | count()
        )
);
print 
    KPI = "Success Rate", Value = round(successRate, 1), Target = 95.0, Unit = "%", Status = iff(successRate >= 95, "‚úÖ Good", iff(successRate >= 90, "‚ö†Ô∏è Warning", "‚ùå Critical"))
union (
    print 
        KPI = "Avg ATS Score", Value = round(avgATSScore, 1), Target = 75.0, Unit = "/100", Status = iff(avgATSScore >= 75, "‚úÖ Good", iff(avgATSScore >= 65, "‚ö†Ô∏è Warning", "‚ùå Critical"))
)
union (
    print 
        KPI = "P95 Processing Time", Value = round(p95ProcessingTime/1000, 1), Target = 30.0, Unit = "sec", Status = iff(p95ProcessingTime <= 30000, "‚úÖ Good", iff(p95ProcessingTime <= 45000, "‚ö†Ô∏è Warning", "‚ùå Critical"))
)
union (
    print 
        KPI = "Error Rate", Value = round(errorRate, 2), Target = 2.0, Unit = "%", Status = iff(errorRate <= 2, "‚úÖ Good", iff(errorRate <= 5, "‚ö†Ô∏è Warning", "‚ùå Critical"))
)
